
public class LoopsidedNumberTriangle {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
     for(int lin_num=1;lin_num<=9;lin_num++) {
    	 for(int char_num=1;char_num<=lin_num;char_num++)
    	 System.out.print(lin_num+" ");
    	 System.out.println(); }
     }
	}


